package com.java.ControlFlow;

public class MaxNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		MAX of two number
		int a=50;
		int b=49;
		int maximum2 = (a>b?a:b);
        System.out.println(maximum2);
//        Max of three number
        int c =90;
        int maximum = (a>b?a:b);
        int finalMaximum = (maximum>c?maximum:c);
        System.out.println(finalMaximum);
        

	}

}
