package com.java.ControlFlow_NestedForLoop_homeWork;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m =5;
		int k =5;
		for(int i=1;i<=5;i++)
		{  
			for(int j =1;j<=i;j++)
			{
				System.out.print(k);
				
			}
			k--;
			System.out.println();
		}

	}

}
